-- 图书管理
CREATE DATABASE books;

USE books;
-- 图书表
CREATE TABLE book(
	id 			INT NOT NULL AUTO_INCREMENT PRIMARY KEY COMMENT '图书编号', 													-- 图书编号
	bookName 	VARCHAR(20)  NOT NULL COMMENT '图书名称',																		-- 图书名称
	authorName 	VARCHAR(15)  NOT NULL COMMENT '作者名称',																		-- 作者名称
	price 		FLOAT  NOT NULL COMMENT '图书售价',																				-- 图书售价
	bookType 	INT  NOT NULL COMMENT '图书类型(1-教学书籍、2-学习书籍、3-科学书籍、4.教育书籍、5-玄幻书籍)',					-- 图书类型
	introduce 	VARCHAR(300) COMMENT '图书介绍',																				-- 图书介绍
	imageName 	VARCHAR(50) NOT NULL COMMENT '图书图片',																		-- 图书图片
	`delete`	INT DEFAULT 0 COMMENT '是否删除(1删除,0未删除)'	   																-- 图书是否被删除
);

-- 清除重置表
TRUNCATE TABLE book;
